mapper.select:
select t.*
from device t

    and t.device_id = #{deviceId}
    and t.agency_id = #{agencyId}
    and t.vehicle_id = #{vehicleId}
    and t.mobile_no = #{mobileNo}
    and t.iccid = #{iccid}
    and t.imei = #{imei}
    and t.maker_id = #{makerId}
    and t.device_model = #{deviceModel}
    and t.protocol_version = #{protocolVersion}
    and t.software_version = #{softwareVersion}
    and t.firmware_version = #{firmwareVersion}
    and t.hardware_version = #{hardwareVersion}
    and t.deleted = #{deleted}

mapper.update:
update device
agency_id = #{agencyId,jdbcType=SMALLINT}, vehicle_id = #{vehicleId,jdbcType=BOOLEAN}, mobile_no = #{mobileNo,jdbcType=VARCHAR}, iccid = #{iccid,jdbcType=CHAR}, imei = #{imei,jdbcType=VARCHAR}, register_time = #{registerTime,jdbcType=TIMESTAMP}, install_time = #{installTime,jdbcType=TIMESTAMP}, protocol_version = #{protocolVersion,jdbcType=TINYINT}, software_version = #{softwareVersion,jdbcType=VARCHAR}, firmware_version = #{firmwareVersion,jdbcType=VARCHAR}, hardware_version = #{hardwareVersion,jdbcType=VARCHAR}, device_model = #{deviceModel,jdbcType=VARCHAR}, maker_id = #{makerId,jdbcType=VARCHAR}, deleted = #{deleted,jdbcType=BOOLEAN}, updated_by=#{updatedBy,jdbcType=VARCHAR},updated_at=now()
where device_id = #{deviceId,jdbcType=VARCHAR}

mapper.insert:
insert ignore into device
agency_id, vehicle_id, mobile_no, iccid, imei, register_time, install_time, protocol_version, software_version, firmware_version, hardware_version, device_model, maker_id, deleted, updated_by,created_by,updated_at,created_at,device_id
#{agencyId,jdbcType=SMALLINT}, #{vehicleId,jdbcType=INTEGER}, #{mobileNo,jdbcType=VARCHAR}, #{iccid,jdbcType=CHAR}, #{imei,jdbcType=VARCHAR}, #{registerTime,jdbcType=TIMESTAMP}, #{installTime,jdbcType=TIMESTAMP}, #{protocolVersion,jdbcType=TINYINT}, #{softwareVersion,jdbcType=VARCHAR}, #{firmwareVersion,jdbcType=VARCHAR}, #{hardwareVersion,jdbcType=VARCHAR}, #{deviceModel,jdbcType=VARCHAR}, #{makerId,jdbcType=VARCHAR}, #{deleted,jdbcType=BOOLEAN}, #{createdBy,jdbcType=VARCHAR},#{createdBy,jdbcType=VARCHAR},now(),now(),#{deviceId,jdbcType=VARCHAR}
