mapper.select:
select t.*
from device_status t

    and t.online = #{online,jdbcType=BIT}
    and t.agency_id = #{agencyId,jdbcType=SMALLINT}
    and t.vehicle_id = #{vehicleId,jdbcType=INTEGER}
    and t.device_id like concat(#{deviceId,jdbcType=VARCHAR},'%')
    and t.mobile_no like concat(#{mobileNo,jdbcType=VARCHAR},'%')
    and t.plate_no like concat(#{plateNo,jdbcType=CHAR},'%')
    and t.address like concat(#{address,jdbcType=VARCHAR},'%')
    and t.speed >= #{speed,jdbcType=SMALLINT}
    and t.updated_at >= #{updatedAt,jdbcType=TIMESTAMP}

mapper.update:
update device_status
online = #{online,jdbcType=BIT}, agency_id = #{agencyId,jdbcType=SMALLINT}, plate_no = #{plateNo,jdbcType=CHAR}, mobile_no = #{mobileNo,jdbcType=VARCHAR}, vehicle_id = #{vehicleId,jdbcType=INTEGER}, updated_at = now()
where device_id = #{deviceId,jdbcType=VARCHAR}
